import React from 'react';
// eslint-disable-next-line no-unused-vars
import Animals from '../Animals/Animals';
// eslint-disable-next-line no-unused-vars
import Species from '../Species/Species';
import './Zoo.module.css';




export default function Zoo() {
   /* Escribe ac치 tu c칩digo */
   const [zoo, setZoo] = React.useState({
      zooName: '',
      animals: [],
      species: [],
      allAnimals: []
      
   });

React.useEffect(() =>{

  return fetch('http://localhost:3001/zoo')
   .then((res) => res.json())
   .then((data) =>
      setZoo({
         ...zoo,
         animals: data.animals,
         species: data.species,
         allAnimals: data.animals,
      })
   )
   .catch((error) => console.log(error));
}, [])
  function handleSpecies  (e){
   setZoo({
      ...zoo,
      animals: zoo.allAnimals.filter(a => a.especie === e.target.value)
  
     })
    };
  function handleAllSpecies  () {
   setZoo({
    ...zoo,
    animals: zoo.allAnimals.filter(a => a.especie === e.target.value)

   })
  };

   //  const handleInputChange = ({target}) => {
   //  setZoo(target.value)   
   // }

      function handleInputChange  (event){
         setZoo({...zoo, zooName: event.target.value})
      }

   return (

      
      <div>
         <label>Zoo Name:</label>
         <input
         type='text' 
         value={zoo.zooName} 
         name='nameInput'
         onChange={handleInputChange} 
         />
         <h1>{zoo.zooName}</h1>
         <Species species={zoo.species} handleSpecies={handleSpecies} handleAllSpecies={handleAllSpecies} />
         <Animals animals={zoo.animals} />

      </div>
   );
}
