import React from 'react'
import './Contact.modules.css'
import { useState } from 'react';

// eslint-disable-next-line
const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

// const [inputs, setInputs] = React.useState({
//   name:'',
