//   subject:'',
//   message:''
// });

// const [error, seterror] = React.useState({
//   name:'',
//   email: '',
//   phone:'',
//   subject:'',
//   message:''
// });



export default function Contact () {

  const [inputs, setInputs] = React.useState({
    name:'',
    email: '',
    phone:0,
    subject:'',
    message:''
  });
  
  const [error, seterror] = React.useState({
    name:'',
    email: '',
    phone:'',
    subject:'',
    message:''
  });
  

  const handleChange = e =>{
   setInputs({
    ...inputs,
    [e.target.name]: e.target.value
   } )
  }
  



  return <div>Crear Formulario





  <form>
    <label>Nombre:</label>
    <input type='text' name='name' placeholder='Escribe tu nombre...' value={inputs.name} onChange={handleChange} />
    <label>Correo Electrónico:</label>
    <input type="text" name='email' placeholder='Escribe tu email...' value={inputs.email} onChange={handleChange} />
    <label>Teléfono:</label>
    <input type='number' name='phone' placeholder='Escribe un teléfono...' value={inputs.phone} onChange={handleChange} />
    <label>Asunto:</label>
    <input type='text' name='subject' placeholder='Escribe el asunto...' value={inputs.subject} onChange={handleChange} />
    <label>Mensaje:</label>
    <textarea type='text' name='message' placeholder='Escribe tu mensaje...' value={inputs.message} onChange={handleChange} />
    <button type='submit' >Enviar</button>
  </form>
  </div>
}
