import React from 'react'
import './Contact.modules.css'
import { useState } from 'react';

// eslint-disable-next-line
const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

const [inputs, setInputs] = React.useState({
  name:'',
  email: '',
  phone:0,
  subject:'',
  message:''
});

const [error, seterror] = React.useState({
  name:'',
  email: '',
  phone:'',
  subject:'',
  message:''
});



export default function Contact () {
  return <div>Crear Formulario

  <form>
    <label>Nombre:</label>
    <input type='text' name='name' placeholder='Escribe tu nombre...' />
    <label>Correo Electrónico:</label>
    <input type="text" name='email' placeholder='Escribe tu email...' />
    <label>Teléfono:</label>
    <input type='number' name='phone' placeholder='Escribe un teléfono...' />
    <label>Asunto:</label>
    <input type='text' name='subject' placeholder='Escribe el asunto...' />
    <label>Mensaje:</label>
    <textarea type='text' name='message' placeholder='Escribe tu mensaje...' />
    <button type='submit' >Enviar</button>
  </form>
  </div>
}
